package MakeSeleniumEasy;

public class Prgm5_1 {
//Swap two integer variables without using third variable
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=20;
		System.out.println(a + " "+b);
		a=a+b;
		b=a-b;
		a=a-b;
		System.out.println(a + " "+b);

	}

}
